INSERT INTO `tmp_hitung` (`kode_penyakit`, `penyakit`, `gejala`, `penanganan`, `probabilitas`, `hasil_probabilitas`, `prosentase`) VALUES
('P01', 'Pulpitis Reversible (Karies/Gigi Berlubang)', 'G10 | Nyeri muncul tiba-tiba.', 'Pulpitis reversible atau juga dikenal dengan istilah karies (gigi berlubang) dapat diatasi dengan mengobati penyebab peradangan, misalnya memberikan tambalan pada gigi berlubang untuk meredakan rasa sakit.', 0.0001, 0, 0.000124956),
('P01', 'Pulpitis Reversible (Karies/Gigi Berlubang)', 'G25 | Gusi kemerahan dan terlihat membulat.', 'Pulpitis reversible atau juga dikenal dengan istilah karies (gigi berlubang) dapat diatasi dengan mengobati penyebab peradangan, misalnya memberikan tambalan pada gigi berlubang untuk meredakan rasa sakit.', 0.0001, 0, 0.000124956),
('P02', 'Peksistensi & Resorbsi Fisiologis', 'G10 | Nyeri muncul tiba-tiba.', 'Persistensi gigi adalah sebuah keadaan dimana gigi susu/kecil tidak tanggal walaupun gigi permanent/dewasa sebagai gantinya sudah mulai tumbuh, terkadang juga gigi susu tidak goyang. Sedangkan resorpsi merupakan kondisi terkait proses fisiologis atau patologis yang mengakibatkan hilangnya dentin, sementum, atau tulang karena interaksi terstruktur antara sel inflamasi dan sel resorpsi, dapat terjadi baik internal maupun eksternal, secara bersamaan pada gigi yang sama, muncul sebagai defek yang terpisah atau bergabung. Jika terjadi nyeri pada gigi dengan gejala seperti yang dirasa untuk sementara dapat mengkonsumsi obat peradangan, apabila keadaan terus memburuk segera kunjungi dokter gigi.', 0.0001, 0.000000001, 0.0000499825),
('P02', 'Peksistensi & Resorbsi Fisiologis', 'G25 | Gusi kemerahan dan terlihat membulat.', 'Persistensi gigi adalah sebuah keadaan dimana gigi susu/kecil tidak tanggal walaupun gigi permanent/dewasa sebagai gantinya sudah mulai tumbuh, terkadang juga gigi susu tidak goyang. Sedangkan resorpsi merupakan kondisi terkait proses fisiologis atau patologis yang mengakibatkan hilangnya dentin, sementum, atau tulang karena interaksi terstruktur antara sel inflamasi dan sel resorpsi, dapat terjadi baik internal maupun eksternal, secara bersamaan pada gigi yang sama, muncul sebagai defek yang terpisah atau bergabung. Jika terjadi nyeri pada gigi dengan gejala seperti yang dirasa untuk sementara dapat mengkonsumsi obat peradangan, apabila keadaan terus memburuk segera kunjungi dokter gigi.', 0.0001, 0.000000001, 0.0000499825),
('P03', 'Pulpitis Irreversible (Lubang Yang Dalam)', 'G10 | Nyeri muncul tiba-tiba.', 'Pulpitis irreversible atau lebih dikenal dengan sebutan karies gigi namun pada kasus gigi berlubang yang dalam, dapat diatasi dengan prosedur pulpektomi, yaitu pengangkatan pulpa mati pada gigi dan akar, serta memasang mahkota baru.', 0.75, 0.000015, 0.749738),
('P03', 'Pulpitis Irreversible (Lubang Yang Dalam)', 'G25 | Gusi kemerahan dan terlihat membulat.', 'Pulpitis irreversible atau lebih dikenal dengan sebutan karies gigi namun pada kasus gigi berlubang yang dalam, dapat diatasi dengan prosedur pulpektomi, yaitu pengangkatan pulpa mati pada gigi dan akar, serta memasang mahkota baru.', 0.0001, 0.000015, 0.749738),
('P04', 'Impaksi Gigi Geraham Ketiga', 'G10 | Nyeri muncul tiba-tiba.', 'Impaksi gigi merupakan kondisi di mana gigi terjebak di dalam gusi sehingga pertumbuhannya tidak sempurna. Kondisi ini kebanyakan terjadi pada gigi bungsu orang dewasa. Impaksi gigi perlu ditangani dengan tepat karena dapat menyebabkan sakit gigi, gigi rusak, maupun penyakit gusi. Untuk meredakan nyeri akibat impaksi gigi dapat dilakukan berbagai hal berikut : 1. Mengompres area yang mengalami nyeri dengan kompres dingin. 2. Berkumur dengan air garam hangat. 3. Mengonsumsi obat pereda nyeri, seperti aspirin. Meski perawatan tersebut dapat membantu meredakan rasa nyeri, Anda tetap disarankan untuk memeriksakan diri ke dokter gigi.', 0.0001, 0.000000001, 0.0000499825),
('P04', 'Impaksi Gigi Geraham Ketiga', 'G25 | Gusi kemerahan dan terlihat membulat.', 'Impaksi gigi merupakan kondisi di mana gigi terjebak di dalam gusi sehingga pertumbuhannya tidak sempurna. Kondisi ini kebanyakan terjadi pada gigi bungsu orang dewasa. Impaksi gigi perlu ditangani dengan tepat karena dapat menyebabkan sakit gigi, gigi rusak, maupun penyakit gusi. Untuk meredakan nyeri akibat impaksi gigi dapat dilakukan berbagai hal berikut : 1. Mengompres area yang mengalami nyeri dengan kompres dingin. 2. Berkumur dengan air garam hangat. 3. Mengonsumsi obat pereda nyeri, seperti aspirin. Meski perawatan tersebut dapat membantu meredakan rasa nyeri, Anda tetap disarankan untuk memeriksakan diri ke dokter gigi.', 0.0001, 0.000000001, 0.0000499825),
('P05', 'Abses (Infeksi Pada Gusi)', 'G10 | Nyeri muncul tiba-tiba.', 'Abses atau infeksi yang terjadi pada gusi, dapat dilakukan penanganan sebagai berikut : 1. Mengeluarkan nanah. Mengeluarkan nanah bertujuan untuk mengeringkan abses dan mengurangi pembengkakan. 2. Menggunakan obat antibiotik. Obat antibiotik digunakan apabila infeksi telah menyebar ke area gigi, rahang atau area lainnya. 3. Mencabut gigi. 4. Perawatan akar gigi.', 0.0001, 0.0000000025, 0.000124956),
('P05', 'Abses (Infeksi Pada Gusi)', 'G25 | Gusi kemerahan dan terlihat membulat.', 'Abses atau infeksi yang terjadi pada gusi, dapat dilakukan penanganan sebagai berikut : 1. Mengeluarkan nanah. Mengeluarkan nanah bertujuan untuk mengeringkan abses dan mengurangi pembengkakan. 2. Menggunakan obat antibiotik. Obat antibiotik digunakan apabila infeksi telah menyebar ke area gigi, rahang atau area lainnya. 3. Mencabut gigi. 4. Perawatan akar gigi.', 0.0001, 0.0000000025, 0.000124956),
('P06', 'Karang Gigi & Staine (Noda Gigi)', 'G25 | Gusi kemerahan dan terlihat membulat.', 'Karang Gigi & Staine atau noda/kotoran yang menempel pada gigi, dapat diatasi dengan cara sebagai berikut : 1. Menyikat gigi secara teratur dua kali sehari, yaitu pada pagi hari setelah sarapan dan pada malam hari sebelum tidur. 2. Berhenti merokok. 3. Membatasi konsumsi teh atau kopi. 4. Mengonsumsi makanan yang kaya akan kalsium, karena kalsium dapat melindungi gigi dari pengeroposan. 5. Mengunjungi dokter gigi untuk dilakukan pembersihan karang gigi dengan jetpump air khusus gigi.', 0.5, 0.000005, 0.249913),
('P06', 'Karang Gigi & Staine (Noda Gigi)', 'G10 | Nyeri muncul tiba-tiba.', 'Karang Gigi & Staine atau noda/kotoran yang menempel pada gigi, dapat diatasi dengan cara sebagai berikut : 1. Menyikat gigi secara teratur dua kali sehari, yaitu pada pagi hari setelah sarapan dan pada malam hari sebelum tidur. 2. Berhenti merokok. 3. Membatasi konsumsi teh atau kopi. 4. Mengonsumsi makanan yang kaya akan kalsium, karena kalsium dapat melindungi gigi dari pengeroposan. 5. Mengunjungi dokter gigi untuk dilakukan pembersihan karang gigi dengan jetpump air khusus gigi.', 0.0001, 0.000005, 0.249913);
